import React, { Component } from "react";
import Video from "./components/Video/Video";
import videoData from "./data/video-details.json";
import Bio from "./components/Bio/Bio";
import SideVideos from "./components/SideVideos/SideVideos";
import Comment from "./components/Comment/Comment";
import Commentlst from "./components/CommentList/CommentList";
import { Api_Url, Api_key } from "./Utils";
import "./App.scss";
import axios from "axios";

class App extends Component {
  state = {
    selectedVideo: null,
    videos: [],
  };

  changeVideo = (id) => {
    axios.get(`${Api_Url}/${id}?api_key${Api_key}`).then((videoDetails) => {
      console.log("Individual movie details", videoDetails.data);
    });

    this.setState({
      selectedVideo: videoDetail.data[0],
      //videos: videoData.filter((vid) => vid.id !== newVideo.id),
    });
  };

  changeDate = (timestamp) => {
    const date = new Date(timestamp);
    const dateFormat =
      date.getMonth() + 1 + "/" + date.getDate() + "/" + date.getFullYear();
    return dateFormat;
  };

  render() {
    return (
      <div className="main">
        <div>
          <Video video={this.state.selectedVideo} date={this.changeDate} />
          <Bio text={this.state.selectedVideo} />
          <Comment />
          <Commentlst
            comment={this.state.selectedVideo}
            date={this.changeDate}
          />
        </div>
        <SideVideos
          videoList={this.state.videos}
          handleClick={this.changeVideo}
        />
      </div>
    );
  }
}

export default App;
