import React, { Component } from "react";
import Video from "./components/Video/Video";
import videoData from "./data/video-details.json";
import Bio from "./components/Bio/Bio";
import SideVideos from "./components/SideVideos/SideVideos";
import Comment from "./components/Comment/Comment";
import Commentlst from "./components/CommentList/CommentList";
import { API_URL, API_KEY } from "./Utils";
import "./App.scss";
import axios from "axios";

class App extends Component {
  state = {
    selectedVideo: null,
    videos: [],
  };

  getVideo = (videoID) => {
    axios
      .get(`${API_URL}/${videoID}?api_key=${API_KEY}`)
      .then((videoDetails) => {
        console.log("videoDetails:", videoDetails.data);

        this.setState({
          selectedVideo: videoDetails.data[0],
        });
      });
  };

  componentDidMount() {
    const currentVideoID = this.props.match.params.videoID;

    console.log("Current video id", currentVideoID);

    axios.get(`${API_URL}?api_key=${API_KEY}`).then((videoResult) => {
      console.log("video result data:", videoResult.data[0].id);

      this.setState({
        videos: videoResult.data.id,
      });

      const firstVideo = videoResult.data.id[0];
      console.log(firstVideo);

      this.getVideo(firstVideo);
    });
  }

  // changeVideo = (id) => {
  //   //Add another axio for all video data
  //   const newVideo = this.state.videos.find((thumbnail) => thumbnail.id === id);

  //   this.setState({
  //     selectedVideo: newVideo,
  //     videos: videoData.filter((vid) => vid.id !== newVideo.id),
  //   });
  // };

  changeDate = (timestamp) => {
    const date = new Date(timestamp);
    const dateFormat =
      date.getMonth() + 1 + "/" + date.getDate() + "/" + date.getFullYear();
    return dateFormat;
  };

  render() {
    //const { videos } = this.state;
    //console.log(videos);
    return (
      <>
        <Video video={this.state.selectedVideo} date={this.changeDate} />
        <main>
          <div className="left">
            <Bio text={this.state.selectedVideo} />
            <Comment />
            <Commentlst
              comment={this.state.selectedVideo}
              date={this.changeDate}
            />
          </div>
          <div className="right">
            <SideVideos
              videoList={this.state.videos}
              handleClick={this.changeVideo}
            />
          </div>
        </main>
      </>
    );
  }
}

export default App;
